contracts/AgentRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title AgentRegistry
 * @notice On-chain registry for AI agent budget management
 * @dev Enables trustless budget enforcement for autonomous AI agents
 *
 * Features:
 * - Daily budget limits per agent
 * - Per-transaction limits
 * - Whitelist/blacklist enforcement
 * - Emergency stop by owner
 * - Automatic daily budget reset
 *
 * Arc Testnet Compatibility:
 * - Supports NATIVE USDC deposits via depositFundsNative() (payable)
 * - Arc uses USDC as the native gas token (like ETH on Ethereum)
 * - ERC20 functions (depositFunds) kept for compatibility with wrapped tokens
 */
contract AgentRegistry is ReentrancyGuard {
    using SafeERC20 for IERC20;

    // ============ Structs ============

    struct AgentConfig {
        address owner;              // Agent's owner (human)
        uint256 dailyBudget;        // Max daily spending (USDC, 6 decimals)
        uint256 perTxLimit;         // Per-transaction limit
        uint256 todaySpent;         // Today's spending
        uint256 lastResetTimestamp; // Last reset timestamp
        bool active;                // Agent active status
    }

    // ============ State ============

    IERC20 public immutable usdc;

    mapping(address => AgentConfig) public agents;
    mapping(address => mapping(address => bool)) public whitelist;
    mapping(address => mapping(address => bool)) public blacklist;
    mapping(address => address[]) public whitelistArray;
    mapping(address => address[]) public blacklistArray;
    mapping(address => uint256) public agentBalances;

    // ============ Events ============

    event AgentRegistered(
        address indexed agent,
        address indexed owner,
        uint256 dailyBudget,
        uint256 perTxLimit
    );
    event AgentUpdated(address indexed agent, uint256 newDailyBudget, uint256 newPerTxLimit);
    event AgentDeactivated(address indexed agent);
    event AgentReactivated(address indexed agent);

    event PaymentExecuted(
        address indexed agent,
        address indexed recipient,
        uint256 amount,
        string memo
    );
    event PaymentBlocked(
        address indexed agent,
        address indexed recipient,
        uint256 amount,
        string reason
    );

    event AddedToWhitelist(address indexed agent, address indexed addr);
    event RemovedFromWhitelist(address indexed agent, address indexed addr);
    event AddedToBlacklist(address indexed agent, address indexed addr);
    event RemovedFromBlacklist(address indexed agent, address indexed addr);

    event BudgetReset(address indexed agent, uint256 previousSpent);
    event FundsDeposited(address indexed agent, uint256 amount);
    event FundsWithdrawn(address indexed agent, uint256 amount);

    // ============ Errors ============

    error NotAgentOwner();
    error AgentNotActive();
    error AgentAlreadyExists();
    error AgentNotFound();
    error RecipientBlacklisted();
    error RecipientNotWhitelisted();
    error DailyBudgetExceeded(uint256 requested, uint256 remaining);
    error PerTxLimitExceeded(uint256 requested, uint256 limit);
    error InsufficientBalance(uint256 requested, uint256 available);
    error InvalidAmount();
    error InvalidAddress();

    // ============ Modifiers ============

    modifier onlyAgentOwner(address agent) {
        if (agents[agent].owner != msg.sender) revert NotAgentOwner();
        _;
    }

    modifier onlyActiveAgent(address agent) {
        if (!agents[agent].active) revert AgentNotActive();
        _;
    }

    // ============ Constructor ============

    constructor(address _usdc) {
        usdc = IERC20(_usdc);
    }

    // ============ Agent Management ============

    /// @notice Register a new agent with budget limits
    /// @param agent The agent's wallet address
    /// @param dailyBudget Maximum daily spending (USDC with 6 decimals)
    /// @param perTxLimit Maximum per-transaction limit
    function registerAgent(
        address agent,
        uint256 dailyBudget,
        uint256 perTxLimit
    ) external {
        if (agent == address(0)) revert InvalidAddress();
        if (agents[agent].owner != address(0)) revert AgentAlreadyExists();

        agents[agent] = AgentConfig({
            owner: msg.sender,
            dailyBudget: dailyBudget,
            perTxLimit: perTxLimit,
            todaySpent: 0,
            lastResetTimestamp: block.timestamp,
            active: true
        });

        emit AgentRegistered(agent, msg.sender, dailyBudget, perTxLimit);
    }

    /// @notice Update agent budget limits
    function updateAgent(
        address agent,
        uint256 newDailyBudget,
        uint256 newPerTxLimit
    ) external onlyAgentOwner(agent) {
        agents[agent].dailyBudget = newDailyBudget;
        agents[agent].perTxLimit = newPerTxLimit;
        emit AgentUpdated(agent, newDailyBudget, newPerTxLimit);
    }

    /// @notice Deactivate an agent (emergency stop)
    function deactivateAgent(address agent) external onlyAgentOwner(agent) {
        agents[agent].active = false;
        emit AgentDeactivated(agent);
    }

    /// @notice Reactivate a deactivated agent
    function reactivateAgent(address agent) external onlyAgentOwner(agent) {
        agents[agent].active = true;
        emit AgentReactivated(agent);
    }

    // ============ Whitelist/Blacklist ============

    function addToWhitelist(address agent, address addr) external onlyAgentOwner(agent) {
        if (!whitelist[agent][addr]) {
            whitelist[agent][addr] = true;
            whitelistArray[agent].push(addr);
            emit AddedToWhitelist(agent, addr);
        }
    }

    function removeFromWhitelist(address agent, address addr) external onlyAgentOwner(agent) {
        if (whitelist[agent][addr]) {
            whitelist[agent][addr] = false;
            emit RemovedFromWhitelist(agent, addr);
        }
    }

    function addToBlacklist(address agent, address addr) external onlyAgentOwner(agent) {
        if (!blacklist[agent][addr]) {
            blacklist[agent][addr] = true;
            blacklistArray[agent].push(addr);
            emit AddedToBlacklist(agent, addr);
        }
    }

    function removeFromBlacklist(address agent, address addr) external onlyAgentOwner(agent) {
        if (blacklist[agent][addr]) {
            blacklist[agent][addr] = false;
            emit RemovedFromBlacklist(agent, addr);
        }
    }

    // ============ Payment Execution ============

    /// @notice Execute a payment from agent's deposited funds
    /// @dev Only the agent itself can call this (agent's private key signs tx)
    function executePayment(
        address recipient,
        uint256 amount,
        string calldata memo
    ) external nonReentrant onlyActiveAgent(msg.sender) {
        if (amount == 0) revert InvalidAmount();
        if (recipient == address(0)) revert InvalidAddress();

        AgentConfig storage config = agents[msg.sender];

        // 1. Blacklist check
        if (blacklist[msg.sender][recipient]) {
            emit PaymentBlocked(msg.sender, recipient, amount, "BLACKLISTED");
            revert RecipientBlacklisted();
        }

        // 2. Whitelist check (if whitelist has entries)
        if (whitelistArray[msg.sender].length > 0 && !whitelist[msg.sender][recipient]) {
            emit PaymentBlocked(msg.sender, recipient, amount, "NOT_WHITELISTED");
            revert RecipientNotWhitelisted();
        }

        // 3. Reset daily budget if new day
        _resetDailyBudgetIfNeeded(msg.sender);

        // 4. Per-transaction limit check
        if (amount > config.perTxLimit) {
            emit PaymentBlocked(msg.sender, recipient, amount, "PER_TX_LIMIT");
            revert PerTxLimitExceeded(amount, config.perTxLimit);
        }

        // 5. Daily budget check
        uint256 remaining = config.dailyBudget - config.todaySpent;
        if (amount > remaining) {
            emit PaymentBlocked(msg.sender, recipient, amount, "DAILY_BUDGET");
            revert DailyBudgetExceeded(amount, remaining);
        }

        // 6. Balance check (use agent's deposited balance)
        uint256 balance = agentBalances[msg.sender];
        if (amount > balance) {
            emit PaymentBlocked(msg.sender, recipient, amount, "INSUFFICIENT_BALANCE");
            revert InsufficientBalance(amount, balance);
        }

        // 7. Update spent amount
        config.todaySpent += amount;

        // 8. Deduct from agent balance and transfer native USDC
        agentBalances[msg.sender] -= amount;

        // Transfer native USDC (Arc Testnet compatible)
        (bool success, ) = payable(recipient).call{value: amount}("");
        require(success, "Native transfer failed");

        emit PaymentExecuted(msg.sender, recipient, amount, memo);
    }

    // ============ Fund Management ============

    /// @notice Deposit ERC20 USDC for agent to use (for wrapped tokens)
    /// @dev Use depositFundsNative() for Arc Testnet native USDC
    function depositFunds(address agent, uint256 amount) external {
        if (agents[agent].owner == address(0)) revert AgentNotFound();
        usdc.safeTransferFrom(msg.sender, address(this), amount);
        agentBalances[agent] += amount;
        emit FundsDeposited(agent, amount);
    }

    /// @notice Deposit NATIVE USDC for agent to use (Arc Testnet compatible)
    /// @dev On Arc, USDC is the native gas token - use this function for deposits
    function depositFundsNative(address agent) external payable {
        if (agents[agent].owner == address(0)) revert AgentNotFound();
        if (msg.value == 0) revert InvalidAmount();
        agentBalances[agent] += msg.value;
        emit FundsDeposited(agent, msg.value);
    }

    /// @notice Withdraw unused funds (owner only) - Native USDC version
    /// @dev Transfers native USDC back to owner
    function withdrawFunds(address agent, uint256 amount) external onlyAgentOwner(agent) nonReentrant {
        if (amount > agentBalances[agent]) revert InsufficientBalance(amount, agentBalances[agent]);
        agentBalances[agent] -= amount;

        // Transfer native USDC
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Native transfer failed");

        emit FundsWithdrawn(agent, amount);
    }

    /// @notice Receive native USDC deposits (fallback for direct transfers)
    receive() external payable {
        // Direct transfers go to the sender's agent balance if registered
        if (agents[msg.sender].owner != address(0)) {
            agentBalances[msg.sender] += msg.value;
            emit FundsDeposited(msg.sender, msg.value);
        }
        // Otherwise just accept the payment (can be withdrawn by contract admin)
    }

    // ============ View Functions ============

    function getAgentConfig(address agent) external view returns (AgentConfig memory) {
        return agents[agent];
    }

    function getAgentBalance(address agent) external view returns (uint256) {
        return agentBalances[agent];
    }

    function getRemainingDailyBudget(address agent) external view returns (uint256) {
        AgentConfig storage config = agents[agent];
        if (_isNewDay(config.lastResetTimestamp)) {
            return config.dailyBudget;
        }
        return config.dailyBudget - config.todaySpent;
    }

    function isWhitelisted(address agent, address addr) external view returns (bool) {
        return whitelist[agent][addr];
    }

    function isBlacklisted(address agent, address addr) external view returns (bool) {
        return blacklist[agent][addr];
    }

    function getWhitelist(address agent) external view returns (address[] memory) {
        return whitelistArray[agent];
    }

    function getBlacklist(address agent) external view returns (address[] memory) {
        return blacklistArray[agent];
    }

    // ============ Internal Functions ============

    function _resetDailyBudgetIfNeeded(address agent) internal {
        AgentConfig storage config = agents[agent];
        if (_isNewDay(config.lastResetTimestamp)) {
            emit BudgetReset(agent, config.todaySpent);
            config.todaySpent = 0;
            config.lastResetTimestamp = block.timestamp;
        }
    }

    function _isNewDay(uint256 lastReset) internal view returns (bool) {
        return block.timestamp / 1 days > lastReset / 1 days;
    }
}

contracts/Escrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ArcPayEscrow
 * @notice Multi-party escrow contract with conditional release for ArcPay SDK
 * @dev Supports native USDC deposits (Arc's native gas token, 18 decimals), milestone-based releases, and dispute resolution
 */
contract ArcPayEscrow is ReentrancyGuard, Ownable {
    // Escrow states
    enum EscrowState {
        Created,
        Funded,
        Released,
        Refunded,
        Disputed,
        Resolved
    }

    // Escrow structure
    struct Escrow {
        bytes32 id;
        address depositor;
        address beneficiary;
        address arbiter;
        uint256 amount;
        uint256 fundedAt;
        uint256 expiresAt;
        EscrowState state;
        string conditionHash; // IPFS hash or condition description
        uint256 releasedAmount;
        uint256 refundedAmount;
    }

    // Milestone structure
    struct Milestone {
        uint256 amount;
        string description;
        bool completed;
        uint256 completedAt;
    }

    // Dispute structure
    struct Dispute {
        bytes32 escrowId;
        address initiator;
        string reason;
        uint256 createdAt;
        bool resolved;
        address winner;
        uint256 depositorShare;
        uint256 beneficiaryShare;
    }

    // State variables
    uint256 public escrowCount;
    uint256 public disputeFee; // Fee in basis points (100 = 1%)
    address public feeCollector;

    // Mappings
    mapping(bytes32 => Escrow) public escrows;
    mapping(bytes32 => Milestone[]) public milestones;
    mapping(bytes32 => Dispute) public disputes;
    mapping(address => bytes32[]) public userEscrows;

    // Events
    event EscrowCreated(
        bytes32 indexed id,
        address indexed depositor,
        address indexed beneficiary,
        address arbiter,
        uint256 amount,
        uint256 expiresAt
    );

    event EscrowFunded(bytes32 indexed id, uint256 amount, uint256 fundedAt);

    event EscrowReleased(
        bytes32 indexed id,
        address indexed beneficiary,
        uint256 amount
    );

    event EscrowRefunded(
        bytes32 indexed id,
        address indexed depositor,
        uint256 amount
    );

    event MilestoneAdded(bytes32 indexed escrowId, uint256 index, uint256 amount);

    event MilestoneCompleted(
        bytes32 indexed escrowId,
        uint256 index,
        uint256 amount
    );

    event DisputeCreated(
        bytes32 indexed escrowId,
        address indexed initiator,
        string reason
    );

    event DisputeResolved(
        bytes32 indexed escrowId,
        address indexed winner,
        uint256 depositorShare,
        uint256 beneficiaryShare
    );

    // Errors
    error InvalidAddress();
    error InvalidAmount();
    error EscrowNotFound();
    error InvalidState();
    error NotAuthorized();
    error AlreadyFunded();
    error NotFunded();
    error EscrowExpired();
    error EscrowNotExpired();
    error DisputeExists();
    error NoDispute();
    error InvalidMilestone();
    error InvalidShares();
    error TransferFailed();

    constructor(address _feeCollector, uint256 _disputeFee) Ownable(msg.sender) {
        if (_feeCollector == address(0)) revert InvalidAddress();
        feeCollector = _feeCollector;
        disputeFee = _disputeFee;
    }

    /**
     * @notice Create a new escrow (without funding)
     * @param beneficiary Address to receive funds on release
     * @param arbiter Address to resolve disputes (can be address(0) for no arbiter)
     * @param amount Amount of native USDC to escrow
     * @param expiresAt Timestamp when escrow expires
     * @param conditionHash IPFS hash or description of release conditions
     */
    function createEscrow(
        address beneficiary,
        address arbiter,
        uint256 amount,
        uint256 expiresAt,
        string calldata conditionHash
    ) external returns (bytes32) {
        if (beneficiary == address(0)) revert InvalidAddress();
        if (amount == 0) revert InvalidAmount();
        if (expiresAt <= block.timestamp) revert EscrowExpired();

        bytes32 id = keccak256(
            abi.encodePacked(
                msg.sender,
                beneficiary,
                amount,
                block.timestamp,
                escrowCount++
            )
        );

        escrows[id] = Escrow({
            id: id,
            depositor: msg.sender,
            beneficiary: beneficiary,
            arbiter: arbiter,
            amount: amount,
            fundedAt: 0,
            expiresAt: expiresAt,
            state: EscrowState.Created,
            conditionHash: conditionHash,
            releasedAmount: 0,
            refundedAmount: 0
        });

        userEscrows[msg.sender].push(id);
        userEscrows[beneficiary].push(id);
        if (arbiter != address(0)) {
            userEscrows[arbiter].push(id);
        }

        emit EscrowCreated(id, msg.sender, beneficiary, arbiter, amount, expiresAt);

        return id;
    }

    /**
     * @notice Fund an escrow with native USDC
     * @dev Accepts native USDC via msg.value (Arc's native gas token, 18 decimals)
     * @param escrowId ID of the escrow to fund
     */
    function fundEscrow(bytes32 escrowId) external payable nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        if (escrow.depositor == address(0)) revert EscrowNotFound();
        if (escrow.state != EscrowState.Created) revert InvalidState();
        if (msg.sender != escrow.depositor) revert NotAuthorized();
        if (block.timestamp >= escrow.expiresAt) revert EscrowExpired();
        if (msg.value != escrow.amount) revert InvalidAmount();

        escrow.state = EscrowState.Funded;
        escrow.fundedAt = block.timestamp;

        emit EscrowFunded(escrowId, escrow.amount, block.timestamp);
    }

    /**
     * @notice Create and fund escrow in one transaction
     * @dev Accepts native USDC via msg.value
     */
    function createAndFundEscrow(
        address beneficiary,
        address arbiter,
        uint256 amount,
        uint256 expiresAt,
        string calldata conditionHash
    ) external payable nonReentrant returns (bytes32) {
        if (beneficiary == address(0)) revert InvalidAddress();
        if (amount == 0) revert InvalidAmount();
        if (msg.value != amount) revert InvalidAmount();
        if (expiresAt <= block.timestamp) revert EscrowExpired();

        bytes32 id = keccak256(
            abi.encodePacked(
                msg.sender,
                beneficiary,
                amount,
                block.timestamp,
                escrowCount++
            )
        );

        escrows[id] = Escrow({
            id: id,
            depositor: msg.sender,
            beneficiary: beneficiary,
            arbiter: arbiter,
            amount: amount,
            fundedAt: block.timestamp,
            expiresAt: expiresAt,
            state: EscrowState.Funded,
            conditionHash: conditionHash,
            releasedAmount: 0,
            refundedAmount: 0
        });

        userEscrows[msg.sender].push(id);
        userEscrows[beneficiary].push(id);
        if (arbiter != address(0)) {
            userEscrows[arbiter].push(id);
        }

        emit EscrowCreated(id, msg.sender, beneficiary, arbiter, amount, expiresAt);
        emit EscrowFunded(id, amount, block.timestamp);

        return id;
    }

    /**
     * @notice Add milestones to an escrow
     * @param escrowId ID of the escrow
     * @param amounts Array of milestone amounts
     * @param descriptions Array of milestone descriptions
     */
    function addMilestones(
        bytes32 escrowId,
        uint256[] calldata amounts,
        string[] calldata descriptions
    ) external {
        Escrow storage escrow = escrows[escrowId];
        if (escrow.depositor == address(0)) revert EscrowNotFound();
        if (msg.sender != escrow.depositor) revert NotAuthorized();
        if (escrow.state != EscrowState.Created && escrow.state != EscrowState.Funded)
            revert InvalidState();
        if (amounts.length != descriptions.length) revert InvalidAmount();

        uint256 totalMilestoneAmount;
        for (uint256 i = 0; i < amounts.length; i++) {
            totalMilestoneAmount += amounts[i];
            milestones[escrowId].push(Milestone({
                amount: amounts[i],
                description: descriptions[i],
                completed: false,
                completedAt: 0
            }));
            emit MilestoneAdded(escrowId, milestones[escrowId].length - 1, amounts[i]);
        }

        if (totalMilestoneAmount != escrow.amount) revert InvalidAmount();
    }

    /**
     * @notice Complete a milestone (depositor confirms work)
     * @param escrowId ID of the escrow
     * @param milestoneIndex Index of the milestone
     */
    function completeMilestone(bytes32 escrowId, uint256 milestoneIndex) external nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        if (escrow.depositor == address(0)) revert EscrowNotFound();
        if (msg.sender != escrow.depositor) revert NotAuthorized();
        if (escrow.state != EscrowState.Funded) revert InvalidState();
        if (milestoneIndex >= milestones[escrowId].length) revert InvalidMilestone();

        Milestone storage milestone = milestones[escrowId][milestoneIndex];
        if (milestone.completed) revert InvalidMilestone();

        milestone.completed = true;
        milestone.completedAt = block.timestamp;
        escrow.releasedAmount += milestone.amount;

        _safeTransfer(escrow.beneficiary, milestone.amount);

        emit MilestoneCompleted(escrowId, milestoneIndex, milestone.amount);

        // Check if all milestones completed
        bool allCompleted = true;
        for (uint256 i = 0; i < milestones[escrowId].length; i++) {
            if (!milestones[escrowId][i].completed) {
                allCompleted = false;
                break;
            }
        }
        if (allCompleted) {
            escrow.state = EscrowState.Released;
        }
    }

    /**
     * @notice Release full escrow to beneficiary (no milestones)
     * @param escrowId ID of the escrow
     */
    function releaseEscrow(bytes32 escrowId) external nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        if (escrow.depositor == address(0)) revert EscrowNotFound();
        if (msg.sender != escrow.depositor) revert NotAuthorized();
        if (escrow.state != EscrowState.Funded) revert InvalidState();
        if (milestones[escrowId].length > 0) revert InvalidState(); // Use milestone completion instead

        uint256 releaseAmount = escrow.amount - escrow.releasedAmount;
        escrow.releasedAmount = escrow.amount;
        escrow.state = EscrowState.Released;

        _safeTransfer(escrow.beneficiary, releaseAmount);

        emit EscrowReleased(escrowId, escrow.beneficiary, releaseAmount);
    }

    /**
     * @notice Refund escrow to depositor (only after expiry)
     * @param escrowId ID of the escrow
     */
    function refundEscrow(bytes32 escrowId) external nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        if (escrow.depositor == address(0)) revert EscrowNotFound();
        if (msg.sender != escrow.depositor) revert NotAuthorized();
        if (escrow.state != EscrowState.Funded) revert InvalidState();
        if (block.timestamp < escrow.expiresAt) revert EscrowNotExpired();

        uint256 refundAmount = escrow.amount - escrow.releasedAmount;
        escrow.refundedAmount = refundAmount;
        escrow.state = EscrowState.Refunded;

        _safeTransfer(escrow.depositor, refundAmount);

        emit EscrowRefunded(escrowId, escrow.depositor, refundAmount);
    }

    /**
     * @notice Create a dispute
     * @param escrowId ID of the escrow
     * @param reason Description of the dispute
     */
    function createDispute(bytes32 escrowId, string calldata reason) external {
        Escrow storage escrow = escrows[escrowId];
        if (escrow.depositor == address(0)) revert EscrowNotFound();
        if (escrow.state != EscrowState.Funded) revert InvalidState();
        if (escrow.arbiter == address(0)) revert NotAuthorized();
        if (msg.sender != escrow.depositor && msg.sender != escrow.beneficiary)
            revert NotAuthorized();
        if (disputes[escrowId].createdAt > 0) revert DisputeExists();

        escrow.state = EscrowState.Disputed;

        disputes[escrowId] = Dispute({
            escrowId: escrowId,
            initiator: msg.sender,
            reason: reason,
            createdAt: block.timestamp,
            resolved: false,
            winner: address(0),
            depositorShare: 0,
            beneficiaryShare: 0
        });

        emit DisputeCreated(escrowId, msg.sender, reason);
    }

    /**
     * @notice Resolve a dispute (arbiter only)
     * @param escrowId ID of the escrow
     * @param depositorShare Percentage for depositor (0-10000, basis points)
     * @param beneficiaryShare Percentage for beneficiary (0-10000, basis points)
     */
    function resolveDispute(
        bytes32 escrowId,
        uint256 depositorShare,
        uint256 beneficiaryShare
    ) external nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        if (escrow.depositor == address(0)) revert EscrowNotFound();
        if (msg.sender != escrow.arbiter) revert NotAuthorized();
        if (escrow.state != EscrowState.Disputed) revert InvalidState();
        if (depositorShare + beneficiaryShare != 10000) revert InvalidShares();

        Dispute storage dispute = disputes[escrowId];
        if (dispute.resolved) revert InvalidState();

        dispute.resolved = true;
        dispute.depositorShare = depositorShare;
        dispute.beneficiaryShare = beneficiaryShare;
        escrow.state = EscrowState.Resolved;

        uint256 remainingAmount = escrow.amount - escrow.releasedAmount;
        uint256 depositorAmount = (remainingAmount * depositorShare) / 10000;
        uint256 beneficiaryAmount = remainingAmount - depositorAmount;

        // Collect dispute fee
        uint256 fee = (remainingAmount * disputeFee) / 10000;
        if (fee > 0) {
            depositorAmount -= fee / 2;
            beneficiaryAmount -= fee / 2;
            _safeTransfer(feeCollector, fee);
        }

        if (depositorAmount > 0) {
            _safeTransfer(escrow.depositor, depositorAmount);
            escrow.refundedAmount = depositorAmount;
        }
        if (beneficiaryAmount > 0) {
            _safeTransfer(escrow.beneficiary, beneficiaryAmount);
            escrow.releasedAmount += beneficiaryAmount;
        }

        address winner = depositorShare > beneficiaryShare
            ? escrow.depositor
            : escrow.beneficiary;

        emit DisputeResolved(escrowId, winner, depositorShare, beneficiaryShare);
    }

    // Internal function for safe native transfers
    function _safeTransfer(address to, uint256 amount) internal {
        (bool success, ) = payable(to).call{value: amount}("");
        if (!success) revert TransferFailed();
    }

    // View functions

    function getEscrow(bytes32 escrowId) external view returns (Escrow memory) {
        return escrows[escrowId];
    }

    function getMilestones(bytes32 escrowId) external view returns (Milestone[] memory) {
        return milestones[escrowId];
    }

    function getDispute(bytes32 escrowId) external view returns (Dispute memory) {
        return disputes[escrowId];
    }

    function getUserEscrows(address user) external view returns (bytes32[] memory) {
        return userEscrows[user];
    }

    // Admin functions

    function setDisputeFee(uint256 _disputeFee) external onlyOwner {
        disputeFee = _disputeFee;
    }

    function setFeeCollector(address _feeCollector) external onlyOwner {
        if (_feeCollector == address(0)) revert InvalidAddress();
        feeCollector = _feeCollector;
    }

    // Allow contract to receive native USDC
    receive() external payable {}
}

contracts/PaymentChannel.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";

/**
 * @title ArcPayPaymentChannel
 * @notice Unidirectional payment channels for micropayments (x402 protocol compatible)
 * @dev Supports native USDC deposits (Arc's native gas token, 18 decimals), off-chain signed payments, and on-chain settlement
 */
contract ArcPayPaymentChannel is ReentrancyGuard {
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;

    // Channel states
    enum ChannelState {
        Open,
        Closing,
        Closed
    }

    // Channel structure
    struct Channel {
        bytes32 id;
        address sender;
        address recipient;
        uint256 deposit;
        uint256 spent;
        uint256 nonce;
        ChannelState state;
        uint256 createdAt;
        uint256 closingAt;
        uint256 closedAt;
    }

    // Constants
    uint256 public constant DISPUTE_PERIOD = 1 hours; // Time to challenge closing
    uint256 public constant MIN_DEPOSIT = 1e15; // 0.001 USDC minimum (18 decimals)

    // State variables
    uint256 public channelCount;

    // Mappings
    mapping(bytes32 => Channel) public channels;
    mapping(address => bytes32[]) public senderChannels;
    mapping(address => bytes32[]) public recipientChannels;

    // Events
    event ChannelOpened(
        bytes32 indexed id,
        address indexed sender,
        address indexed recipient,
        uint256 deposit
    );

    event ChannelTopUp(bytes32 indexed id, uint256 additionalDeposit, uint256 newTotal);

    event ChannelClosing(bytes32 indexed id, uint256 spent, uint256 closingAt);

    event ChannelClosed(
        bytes32 indexed id,
        uint256 senderRefund,
        uint256 recipientPayment
    );

    event ChannelDisputed(bytes32 indexed id, uint256 newSpent, uint256 newNonce);

    event PaymentSigned(
        bytes32 indexed channelId,
        uint256 amount,
        uint256 totalSpent,
        uint256 nonce
    );

    // Errors
    error InvalidAddress();
    error InvalidAmount();
    error InvalidDeposit();
    error ChannelNotFound();
    error InvalidState();
    error NotAuthorized();
    error InvalidSignature();
    error InvalidNonce();
    error DisputePeriodActive();
    error DisputePeriodExpired();
    error InsufficientBalance();
    error TransferFailed();

    /**
     * @notice Open a new payment channel
     * @dev Accepts native USDC via msg.value (Arc's native gas token, 18 decimals)
     * @param recipient Address to receive payments
     */
    function openChannel(
        address recipient,
        uint256 deposit
    ) external payable nonReentrant returns (bytes32) {
        if (recipient == address(0)) revert InvalidAddress();
        if (recipient == msg.sender) revert InvalidAddress();
        if (msg.value < MIN_DEPOSIT) revert InvalidDeposit();
        if (msg.value != deposit) revert InvalidAmount();

        bytes32 id = keccak256(
            abi.encodePacked(
                msg.sender,
                recipient,
                block.timestamp,
                channelCount++
            )
        );

        channels[id] = Channel({
            id: id,
            sender: msg.sender,
            recipient: recipient,
            deposit: deposit,
            spent: 0,
            nonce: 0,
            state: ChannelState.Open,
            createdAt: block.timestamp,
            closingAt: 0,
            closedAt: 0
        });

        senderChannels[msg.sender].push(id);
        recipientChannels[recipient].push(id);

        emit ChannelOpened(id, msg.sender, recipient, deposit);

        return id;
    }

    /**
     * @notice Top up an existing channel with more native USDC
     * @dev Accepts native USDC via msg.value
     * @param channelId ID of the channel
     */
    function topUpChannel(bytes32 channelId) external payable nonReentrant {
        Channel storage channel = channels[channelId];
        if (channel.sender == address(0)) revert ChannelNotFound();
        if (msg.sender != channel.sender) revert NotAuthorized();
        if (channel.state != ChannelState.Open) revert InvalidState();
        if (msg.value == 0) revert InvalidAmount();

        channel.deposit += msg.value;

        emit ChannelTopUp(channelId, msg.value, channel.deposit);
    }

    /**
     * @notice Initiate channel closing (recipient presents final signed payment)
     * @param channelId ID of the channel
     * @param spent Total amount spent in channel
     * @param nonce Payment nonce
     * @param signature Sender's signature on the payment
     */
    function closeChannel(
        bytes32 channelId,
        uint256 spent,
        uint256 nonce,
        bytes calldata signature
    ) external nonReentrant {
        Channel storage channel = channels[channelId];
        if (channel.sender == address(0)) revert ChannelNotFound();
        if (msg.sender != channel.recipient) revert NotAuthorized();
        if (channel.state != ChannelState.Open) revert InvalidState();
        if (spent > channel.deposit) revert InsufficientBalance();

        // Verify signature
        bytes32 messageHash = getPaymentHash(channelId, spent, nonce);
        address signer = messageHash.toEthSignedMessageHash().recover(signature);
        if (signer != channel.sender) revert InvalidSignature();

        channel.spent = spent;
        channel.nonce = nonce;
        channel.state = ChannelState.Closing;
        channel.closingAt = block.timestamp + DISPUTE_PERIOD;

        emit ChannelClosing(channelId, spent, channel.closingAt);
    }

    /**
     * @notice Challenge a closing with a higher nonce payment
     * @param channelId ID of the channel
     * @param spent Total amount spent
     * @param nonce Payment nonce (must be higher than current)
     * @param signature Sender's signature
     */
    function disputeChannel(
        bytes32 channelId,
        uint256 spent,
        uint256 nonce,
        bytes calldata signature
    ) external {
        Channel storage channel = channels[channelId];
        if (channel.sender == address(0)) revert ChannelNotFound();
        if (channel.state != ChannelState.Closing) revert InvalidState();
        if (block.timestamp >= channel.closingAt) revert DisputePeriodExpired();
        if (nonce <= channel.nonce) revert InvalidNonce();
        if (spent > channel.deposit) revert InsufficientBalance();

        // Verify signature
        bytes32 messageHash = getPaymentHash(channelId, spent, nonce);
        address signer = messageHash.toEthSignedMessageHash().recover(signature);
        if (signer != channel.sender) revert InvalidSignature();

        channel.spent = spent;
        channel.nonce = nonce;

        emit ChannelDisputed(channelId, spent, nonce);
    }

    /**
     * @notice Finalize channel close after dispute period
     * @param channelId ID of the channel
     */
    function finalizeClose(bytes32 channelId) external nonReentrant {
        Channel storage channel = channels[channelId];
        if (channel.sender == address(0)) revert ChannelNotFound();
        if (channel.state != ChannelState.Closing) revert InvalidState();
        if (block.timestamp < channel.closingAt) revert DisputePeriodActive();

        channel.state = ChannelState.Closed;
        channel.closedAt = block.timestamp;

        uint256 recipientAmount = channel.spent;
        uint256 senderRefund = channel.deposit - channel.spent;

        if (recipientAmount > 0) {
            _safeTransfer(channel.recipient, recipientAmount);
        }
        if (senderRefund > 0) {
            _safeTransfer(channel.sender, senderRefund);
        }

        emit ChannelClosed(channelId, senderRefund, recipientAmount);
    }

    /**
     * @notice Cooperative close (both parties agree)
     * @param channelId ID of the channel
     * @param spent Final spent amount
     * @param senderSignature Sender's signature
     * @param recipientSignature Recipient's signature
     */
    function cooperativeClose(
        bytes32 channelId,
        uint256 spent,
        bytes calldata senderSignature,
        bytes calldata recipientSignature
    ) external nonReentrant {
        Channel storage channel = channels[channelId];
        if (channel.sender == address(0)) revert ChannelNotFound();
        if (channel.state != ChannelState.Open && channel.state != ChannelState.Closing)
            revert InvalidState();
        if (spent > channel.deposit) revert InsufficientBalance();

        // Verify both signatures
        bytes32 closeHash = keccak256(
            abi.encodePacked(channelId, spent, "cooperative_close")
        );

        address senderSigner = closeHash.toEthSignedMessageHash().recover(senderSignature);
        address recipientSigner = closeHash.toEthSignedMessageHash().recover(recipientSignature);

        if (senderSigner != channel.sender) revert InvalidSignature();
        if (recipientSigner != channel.recipient) revert InvalidSignature();

        channel.spent = spent;
        channel.state = ChannelState.Closed;
        channel.closedAt = block.timestamp;

        uint256 recipientAmount = spent;
        uint256 senderRefund = channel.deposit - spent;

        if (recipientAmount > 0) {
            _safeTransfer(channel.recipient, recipientAmount);
        }
        if (senderRefund > 0) {
            _safeTransfer(channel.sender, senderRefund);
        }

        emit ChannelClosed(channelId, senderRefund, recipientAmount);
    }

    /**
     * @notice Emergency close by sender (forfeits all funds to recipient)
     * @param channelId ID of the channel
     */
    function emergencyClose(bytes32 channelId) external nonReentrant {
        Channel storage channel = channels[channelId];
        if (channel.sender == address(0)) revert ChannelNotFound();
        if (msg.sender != channel.sender) revert NotAuthorized();
        if (channel.state == ChannelState.Closed) revert InvalidState();

        channel.spent = channel.deposit;
        channel.state = ChannelState.Closed;
        channel.closedAt = block.timestamp;

        _safeTransfer(channel.recipient, channel.deposit);

        emit ChannelClosed(channelId, 0, channel.deposit);
    }

    // Internal function for safe native transfers
    function _safeTransfer(address to, uint256 amount) internal {
        (bool success, ) = payable(to).call{value: amount}("");
        if (!success) revert TransferFailed();
    }

    // Helper functions

    /**
     * @notice Get payment message hash for signing
     * @param channelId Channel ID
     * @param spent Total spent amount
     * @param nonce Payment nonce
     */
    function getPaymentHash(
        bytes32 channelId,
        uint256 spent,
        uint256 nonce
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(channelId, spent, nonce));
    }

    /**
     * @notice Verify a payment signature (used off-chain by recipient)
     * @param channelId Channel ID
     * @param spent Total spent amount
     * @param nonce Payment nonce
     * @param signature Sender's signature
     */
    function verifyPayment(
        bytes32 channelId,
        uint256 spent,
        uint256 nonce,
        bytes calldata signature
    ) external view returns (bool) {
        Channel storage channel = channels[channelId];
        if (channel.sender == address(0)) return false;
        if (spent > channel.deposit) return false;

        bytes32 messageHash = getPaymentHash(channelId, spent, nonce);
        address signer = messageHash.toEthSignedMessageHash().recover(signature);

        return signer == channel.sender;
    }

    // View functions

    function getChannel(bytes32 channelId) external view returns (Channel memory) {
        return channels[channelId];
    }

    function getSenderChannels(address sender) external view returns (bytes32[] memory) {
        return senderChannels[sender];
    }

    function getRecipientChannels(address recipient) external view returns (bytes32[] memory) {
        return recipientChannels[recipient];
    }

    function getChannelBalance(bytes32 channelId) external view returns (uint256 available, uint256 spent) {
        Channel storage channel = channels[channelId];
        available = channel.deposit - channel.spent;
        spent = channel.spent;
    }

    // Allow contract to receive native USDC
    receive() external payable {}
}

contracts/StealthRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ArcPayStealthRegistry
 * @notice Stealth address registry for private USDC payments
 * @dev Implements EIP-5564 compatible stealth addresses for Arc blockchain
 * @dev Uses native USDC (Arc's gas token, 18 decimals) via payable functions
 */
contract ArcPayStealthRegistry is ReentrancyGuard {

    // Stealth meta-address components
    struct StealthMetaAddress {
        bytes spendingPubKey; // Compressed public key for spending
        bytes viewingPubKey; // Compressed public key for viewing
        uint256 registeredAt;
        bool active;
    }

    // Stealth payment announcement
    struct Announcement {
        bytes32 id;
        address stealthAddress;
        uint256 amount;
        bytes ephemeralPubKey; // Ephemeral public key for deriving shared secret
        bytes encryptedMemo; // Optional encrypted memo
        uint256 timestamp;
        bool claimed;
    }

    // State variables
    uint256 public announcementCount;

    // Mappings
    mapping(address => StealthMetaAddress) public metaAddresses;
    mapping(bytes32 => Announcement) public announcements;
    mapping(address => bytes32[]) public userAnnouncements; // announcements where user might be recipient

    // Indexes for scanning
    bytes32[] public allAnnouncements;
    mapping(address => uint256) public lastScannedIndex;

    // Events
    event MetaAddressRegistered(
        address indexed user,
        bytes spendingPubKey,
        bytes viewingPubKey
    );

    event MetaAddressUpdated(
        address indexed user,
        bytes spendingPubKey,
        bytes viewingPubKey
    );

    event MetaAddressDeactivated(address indexed user);

    event StealthPayment(
        bytes32 indexed id,
        address indexed stealthAddress,
        uint256 amount,
        bytes ephemeralPubKey,
        bytes encryptedMemo
    );

    event PaymentClaimed(
        bytes32 indexed id,
        address indexed stealthAddress,
        address indexed claimer,
        uint256 amount
    );

    // Errors
    error InvalidAddress();
    error InvalidPubKey();
    error InvalidAmount();
    error MetaAddressNotRegistered();
    error MetaAddressAlreadyRegistered();
    error MetaAddressInactive();
    error AnnouncementNotFound();
    error AlreadyClaimed();
    error NotAuthorized();
    error TransferFailed();

    constructor() {
        // No parameters needed - uses native USDC
    }

    /**
     * @notice Register a stealth meta-address
     * @param spendingPubKey Compressed spending public key (33 bytes)
     * @param viewingPubKey Compressed viewing public key (33 bytes)
     */
    function registerMetaAddress(
        bytes calldata spendingPubKey,
        bytes calldata viewingPubKey
    ) external {
        if (spendingPubKey.length != 33) revert InvalidPubKey();
        if (viewingPubKey.length != 33) revert InvalidPubKey();
        if (metaAddresses[msg.sender].registeredAt > 0)
            revert MetaAddressAlreadyRegistered();

        metaAddresses[msg.sender] = StealthMetaAddress({
            spendingPubKey: spendingPubKey,
            viewingPubKey: viewingPubKey,
            registeredAt: block.timestamp,
            active: true
        });

        emit MetaAddressRegistered(msg.sender, spendingPubKey, viewingPubKey);
    }

    /**
     * @notice Update stealth meta-address
     * @param spendingPubKey New spending public key
     * @param viewingPubKey New viewing public key
     */
    function updateMetaAddress(
        bytes calldata spendingPubKey,
        bytes calldata viewingPubKey
    ) external {
        if (spendingPubKey.length != 33) revert InvalidPubKey();
        if (viewingPubKey.length != 33) revert InvalidPubKey();
        if (metaAddresses[msg.sender].registeredAt == 0)
            revert MetaAddressNotRegistered();

        StealthMetaAddress storage meta = metaAddresses[msg.sender];
        meta.spendingPubKey = spendingPubKey;
        meta.viewingPubKey = viewingPubKey;

        emit MetaAddressUpdated(msg.sender, spendingPubKey, viewingPubKey);
    }

    /**
     * @notice Deactivate meta-address
     */
    function deactivateMetaAddress() external {
        if (metaAddresses[msg.sender].registeredAt == 0)
            revert MetaAddressNotRegistered();

        metaAddresses[msg.sender].active = false;

        emit MetaAddressDeactivated(msg.sender);
    }

    /**
     * @notice Reactivate meta-address
     */
    function reactivateMetaAddress() external {
        if (metaAddresses[msg.sender].registeredAt == 0)
            revert MetaAddressNotRegistered();

        metaAddresses[msg.sender].active = true;
    }

    /**
     * @notice Send a stealth payment
     * @dev Accepts native USDC via msg.value
     * @param stealthAddress Derived stealth address to receive funds
     * @param ephemeralPubKey Ephemeral public key for recipient to derive shared secret
     * @param encryptedMemo Optional encrypted memo (can be empty)
     */
    function sendStealthPayment(
        address stealthAddress,
        bytes calldata ephemeralPubKey,
        bytes calldata encryptedMemo
    ) external payable nonReentrant returns (bytes32) {
        if (stealthAddress == address(0)) revert InvalidAddress();
        if (msg.value == 0) revert InvalidAmount();
        if (ephemeralPubKey.length != 33) revert InvalidPubKey();

        bytes32 id = keccak256(
            abi.encodePacked(
                msg.sender,
                stealthAddress,
                msg.value,
                block.timestamp,
                announcementCount++
            )
        );

        announcements[id] = Announcement({
            id: id,
            stealthAddress: stealthAddress,
            amount: msg.value,
            ephemeralPubKey: ephemeralPubKey,
            encryptedMemo: encryptedMemo,
            timestamp: block.timestamp,
            claimed: false
        });

        allAnnouncements.push(id);

        // Transfer native USDC to stealth address
        (bool success, ) = payable(stealthAddress).call{value: msg.value}("");
        if (!success) revert TransferFailed();

        emit StealthPayment(
            id,
            stealthAddress,
            msg.value,
            ephemeralPubKey,
            encryptedMemo
        );

        return id;
    }

    /**
     * @notice Mark a payment as claimed (called by recipient after claiming)
     * @param announcementId ID of the announcement
     * @dev This is for tracking purposes - actual funds are already at stealth address
     */
    function markClaimed(bytes32 announcementId) external {
        Announcement storage announcement = announcements[announcementId];
        if (announcement.timestamp == 0) revert AnnouncementNotFound();
        if (msg.sender != announcement.stealthAddress) revert NotAuthorized();
        if (announcement.claimed) revert AlreadyClaimed();

        announcement.claimed = true;

        emit PaymentClaimed(
            announcementId,
            announcement.stealthAddress,
            msg.sender,
            announcement.amount
        );
    }

    // View functions

    /**
     * @notice Get meta-address for a user
     */
    function getMetaAddress(address user)
        external
        view
        returns (StealthMetaAddress memory)
    {
        return metaAddresses[user];
    }

    /**
     * @notice Get announcement by ID
     */
    function getAnnouncement(bytes32 id)
        external
        view
        returns (Announcement memory)
    {
        return announcements[id];
    }

    /**
     * @notice Get all announcements for scanning
     * @param fromIndex Start index
     * @param count Number of announcements to return
     */
    function getAnnouncements(uint256 fromIndex, uint256 count)
        external
        view
        returns (Announcement[] memory)
    {
        if (fromIndex >= allAnnouncements.length) {
            return new Announcement[](0);
        }

        uint256 endIndex = fromIndex + count;
        if (endIndex > allAnnouncements.length) {
            endIndex = allAnnouncements.length;
        }

        uint256 resultCount = endIndex - fromIndex;
        Announcement[] memory result = new Announcement[](resultCount);

        for (uint256 i = 0; i < resultCount; i++) {
            result[i] = announcements[allAnnouncements[fromIndex + i]];
        }

        return result;
    }

    /**
     * @notice Get new announcements since last scan
     * @param count Max number of announcements to return
     */
    function getNewAnnouncements(uint256 count)
        external
        view
        returns (Announcement[] memory, uint256 newLastIndex)
    {
        uint256 startIndex = lastScannedIndex[msg.sender];
        uint256 total = allAnnouncements.length;

        if (startIndex >= total) {
            return (new Announcement[](0), startIndex);
        }

        uint256 available = total - startIndex;
        uint256 fetchCount = available < count ? available : count;

        Announcement[] memory result = new Announcement[](fetchCount);
        for (uint256 i = 0; i < fetchCount; i++) {
            result[i] = announcements[allAnnouncements[startIndex + i]];
        }

        newLastIndex = startIndex + fetchCount;
        return (result, newLastIndex);
    }

    /**
     * @notice Update last scanned index
     */
    function updateLastScanned(uint256 index) external {
        if (index > allAnnouncements.length) {
            index = allAnnouncements.length;
        }
        lastScannedIndex[msg.sender] = index;
    }

    /**
     * @notice Get total number of announcements
     */
    function getTotalAnnouncements() external view returns (uint256) {
        return allAnnouncements.length;
    }

    /**
     * @notice Check if an address has a registered meta-address
     */
    function isRegistered(address user) external view returns (bool) {
        return metaAddresses[user].registeredAt > 0 && metaAddresses[user].active;
    }

    // Allow contract to receive native USDC
    receive() external payable {}
}

contracts/StreamPayment.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ArcPayStreamPayment
 * @notice Real-time streaming payments for ArcPay SDK
 * @dev Supports per-second native USDC streaming with pause/resume functionality
 * @dev On Arc, USDC is the native gas token (18 decimals)
 */
contract ArcPayStreamPayment is ReentrancyGuard {
    // Stream states
    enum StreamState {
        Active,
        Paused,
        Cancelled,
        Completed
    }

    // Stream structure
    struct Stream {
        bytes32 id;
        address sender;
        address recipient;
        uint256 totalAmount;
        uint256 claimedAmount;
        uint256 ratePerSecond;
        uint256 startTime;
        uint256 endTime;
        uint256 pausedAt;
        uint256 pausedDuration;
        StreamState state;
    }

    // State variables
    uint256 public streamCount;
    uint256 public protocolFee; // Basis points (100 = 1%)
    address public feeCollector;

    // Mappings
    mapping(bytes32 => Stream) public streams;
    mapping(address => bytes32[]) public senderStreams;
    mapping(address => bytes32[]) public recipientStreams;

    // Events
    event StreamCreated(
        bytes32 indexed id,
        address indexed sender,
        address indexed recipient,
        uint256 totalAmount,
        uint256 ratePerSecond,
        uint256 startTime,
        uint256 endTime
    );

    event StreamClaimed(bytes32 indexed id, address indexed recipient, uint256 amount);

    event StreamPaused(bytes32 indexed id, uint256 pausedAt);

    event StreamResumed(bytes32 indexed id, uint256 resumedAt, uint256 newEndTime);

    event StreamCancelled(
        bytes32 indexed id,
        uint256 senderRefund,
        uint256 recipientPayment
    );

    event StreamCompleted(bytes32 indexed id, uint256 totalClaimed);

    event StreamToppedUp(bytes32 indexed id, uint256 addedAmount, uint256 newEndTime);

    // Errors
    error InvalidAddress();
    error InvalidAmount();
    error InvalidDuration();
    error StreamNotFound();
    error InvalidState();
    error NotAuthorized();
    error NothingToClaim();
    error StreamNotStarted();
    error StreamEnded();
    error TransferFailed();

    constructor(address _feeCollector, uint256 _protocolFee) {
        if (_feeCollector == address(0)) revert InvalidAddress();
        feeCollector = _feeCollector;
        protocolFee = _protocolFee;
    }

    /**
     * @notice Create a new payment stream
     * @dev Accepts native USDC via msg.value (Arc's native gas token, 18 decimals)
     * @param recipient Address to receive streamed funds
     * @param duration Stream duration in seconds
     */
    function createStream(
        address recipient,
        uint256 totalAmount,
        uint256 duration
    ) external payable nonReentrant returns (bytes32) {
        if (recipient == address(0)) revert InvalidAddress();
        if (recipient == msg.sender) revert InvalidAddress();
        if (msg.value == 0) revert InvalidAmount();
        if (msg.value != totalAmount) revert InvalidAmount();
        if (duration == 0) revert InvalidDuration();

        uint256 ratePerSecond = totalAmount / duration;
        if (ratePerSecond == 0) revert InvalidAmount();

        bytes32 id = keccak256(
            abi.encodePacked(
                msg.sender,
                recipient,
                totalAmount,
                block.timestamp,
                streamCount++
            )
        );

        uint256 startTime = block.timestamp;
        uint256 endTime = startTime + duration;

        streams[id] = Stream({
            id: id,
            sender: msg.sender,
            recipient: recipient,
            totalAmount: totalAmount,
            claimedAmount: 0,
            ratePerSecond: ratePerSecond,
            startTime: startTime,
            endTime: endTime,
            pausedAt: 0,
            pausedDuration: 0,
            state: StreamState.Active
        });

        senderStreams[msg.sender].push(id);
        recipientStreams[recipient].push(id);

        emit StreamCreated(
            id,
            msg.sender,
            recipient,
            totalAmount,
            ratePerSecond,
            startTime,
            endTime
        );

        return id;
    }

    /**
     * @notice Create a stream starting at a future time
     * @dev Accepts native USDC via msg.value
     */
    function createScheduledStream(
        address recipient,
        uint256 totalAmount,
        uint256 startTime,
        uint256 endTime
    ) external payable nonReentrant returns (bytes32) {
        if (recipient == address(0)) revert InvalidAddress();
        if (recipient == msg.sender) revert InvalidAddress();
        if (msg.value == 0) revert InvalidAmount();
        if (msg.value != totalAmount) revert InvalidAmount();
        if (startTime < block.timestamp) revert InvalidDuration();
        if (endTime <= startTime) revert InvalidDuration();

        uint256 duration = endTime - startTime;
        uint256 ratePerSecond = totalAmount / duration;
        if (ratePerSecond == 0) revert InvalidAmount();

        bytes32 id = keccak256(
            abi.encodePacked(
                msg.sender,
                recipient,
                totalAmount,
                block.timestamp,
                streamCount++
            )
        );

        streams[id] = Stream({
            id: id,
            sender: msg.sender,
            recipient: recipient,
            totalAmount: totalAmount,
            claimedAmount: 0,
            ratePerSecond: ratePerSecond,
            startTime: startTime,
            endTime: endTime,
            pausedAt: 0,
            pausedDuration: 0,
            state: StreamState.Active
        });

        senderStreams[msg.sender].push(id);
        recipientStreams[recipient].push(id);

        emit StreamCreated(
            id,
            msg.sender,
            recipient,
            totalAmount,
            ratePerSecond,
            startTime,
            endTime
        );

        return id;
    }

    /**
     * @notice Claim available streamed funds
     * @param streamId ID of the stream
     */
    function claim(bytes32 streamId) external nonReentrant returns (uint256) {
        Stream storage stream = streams[streamId];
        if (stream.sender == address(0)) revert StreamNotFound();
        if (msg.sender != stream.recipient) revert NotAuthorized();
        if (stream.state == StreamState.Cancelled || stream.state == StreamState.Completed)
            revert InvalidState();

        uint256 claimable = getClaimableAmount(streamId);
        if (claimable == 0) revert NothingToClaim();

        stream.claimedAmount += claimable;

        // Check if stream is complete
        if (stream.claimedAmount >= stream.totalAmount) {
            stream.state = StreamState.Completed;
            emit StreamCompleted(streamId, stream.claimedAmount);
        }

        // Calculate and deduct protocol fee
        uint256 fee = (claimable * protocolFee) / 10000;
        uint256 recipientAmount = claimable - fee;

        if (fee > 0) {
            _safeTransfer(feeCollector, fee);
        }
        _safeTransfer(stream.recipient, recipientAmount);

        emit StreamClaimed(streamId, stream.recipient, claimable);

        return claimable;
    }

    /**
     * @notice Pause a stream (sender only)
     * @param streamId ID of the stream
     */
    function pauseStream(bytes32 streamId) external {
        Stream storage stream = streams[streamId];
        if (stream.sender == address(0)) revert StreamNotFound();
        if (msg.sender != stream.sender) revert NotAuthorized();
        if (stream.state != StreamState.Active) revert InvalidState();

        stream.state = StreamState.Paused;
        stream.pausedAt = block.timestamp;

        emit StreamPaused(streamId, block.timestamp);
    }

    /**
     * @notice Resume a paused stream (sender only)
     * @param streamId ID of the stream
     */
    function resumeStream(bytes32 streamId) external {
        Stream storage stream = streams[streamId];
        if (stream.sender == address(0)) revert StreamNotFound();
        if (msg.sender != stream.sender) revert NotAuthorized();
        if (stream.state != StreamState.Paused) revert InvalidState();

        uint256 pauseDuration = block.timestamp - stream.pausedAt;
        stream.pausedDuration += pauseDuration;
        stream.endTime += pauseDuration;
        stream.pausedAt = 0;
        stream.state = StreamState.Active;

        emit StreamResumed(streamId, block.timestamp, stream.endTime);
    }

    /**
     * @notice Cancel a stream (sender only)
     * @param streamId ID of the stream
     */
    function cancelStream(bytes32 streamId) external nonReentrant {
        Stream storage stream = streams[streamId];
        if (stream.sender == address(0)) revert StreamNotFound();
        if (msg.sender != stream.sender) revert NotAuthorized();
        if (stream.state == StreamState.Cancelled || stream.state == StreamState.Completed)
            revert InvalidState();

        uint256 claimable = getClaimableAmount(streamId);
        uint256 recipientAmount = claimable;
        uint256 senderRefund = stream.totalAmount - stream.claimedAmount - claimable;

        stream.state = StreamState.Cancelled;
        stream.claimedAmount += claimable;

        if (recipientAmount > 0) {
            // Apply protocol fee
            uint256 fee = (recipientAmount * protocolFee) / 10000;
            if (fee > 0) {
                _safeTransfer(feeCollector, fee);
                recipientAmount -= fee;
            }
            _safeTransfer(stream.recipient, recipientAmount);
        }

        if (senderRefund > 0) {
            _safeTransfer(stream.sender, senderRefund);
        }

        emit StreamCancelled(streamId, senderRefund, claimable);
    }

    /**
     * @notice Top up a stream with additional funds
     * @dev Accepts native USDC via msg.value
     * @param streamId ID of the stream
     */
    function topUpStream(bytes32 streamId) external payable nonReentrant {
        Stream storage stream = streams[streamId];
        if (stream.sender == address(0)) revert StreamNotFound();
        if (msg.sender != stream.sender) revert NotAuthorized();
        if (stream.state != StreamState.Active && stream.state != StreamState.Paused)
            revert InvalidState();
        if (msg.value == 0) revert InvalidAmount();

        uint256 additionalAmount = msg.value;
        stream.totalAmount += additionalAmount;

        // Extend end time proportionally
        uint256 additionalDuration = additionalAmount / stream.ratePerSecond;
        stream.endTime += additionalDuration;

        emit StreamToppedUp(streamId, additionalAmount, stream.endTime);
    }

    // Internal function for safe native transfers
    function _safeTransfer(address to, uint256 amount) internal {
        (bool success, ) = payable(to).call{value: amount}("");
        if (!success) revert TransferFailed();
    }

    // View functions

    /**
     * @notice Get claimable amount for a stream
     */
    function getClaimableAmount(bytes32 streamId) public view returns (uint256) {
        Stream storage stream = streams[streamId];
        if (stream.sender == address(0)) return 0;
        if (stream.state == StreamState.Cancelled || stream.state == StreamState.Completed)
            return 0;

        uint256 effectiveTime;
        if (stream.state == StreamState.Paused) {
            effectiveTime = stream.pausedAt;
        } else {
            effectiveTime = block.timestamp;
        }

        if (effectiveTime < stream.startTime) return 0;

        uint256 elapsedTime;
        if (effectiveTime >= stream.endTime) {
            elapsedTime = stream.endTime - stream.startTime - stream.pausedDuration;
        } else {
            elapsedTime = effectiveTime - stream.startTime - stream.pausedDuration;
        }

        uint256 totalStreamed = elapsedTime * stream.ratePerSecond;
        if (totalStreamed > stream.totalAmount) {
            totalStreamed = stream.totalAmount;
        }

        if (totalStreamed <= stream.claimedAmount) return 0;

        return totalStreamed - stream.claimedAmount;
    }

    /**
     * @notice Get stream details
     */
    function getStream(bytes32 streamId) external view returns (Stream memory) {
        return streams[streamId];
    }

    /**
     * @notice Get all streams for a sender
     */
    function getSenderStreams(address sender) external view returns (bytes32[] memory) {
        return senderStreams[sender];
    }

    /**
     * @notice Get all streams for a recipient
     */
    function getRecipientStreams(address recipient) external view returns (bytes32[] memory) {
        return recipientStreams[recipient];
    }

    /**
     * @notice Get stream progress
     */
    function getStreamProgress(bytes32 streamId)
        external
        view
        returns (
            uint256 totalAmount,
            uint256 claimedAmount,
            uint256 claimableAmount,
            uint256 remainingAmount,
            uint256 percentComplete
        )
    {
        Stream storage stream = streams[streamId];
        totalAmount = stream.totalAmount;
        claimedAmount = stream.claimedAmount;
        claimableAmount = getClaimableAmount(streamId);
        remainingAmount = totalAmount - claimedAmount - claimableAmount;
        percentComplete = totalAmount > 0
            ? ((claimedAmount + claimableAmount) * 100) / totalAmount
            : 0;
    }

    // Admin functions

    function setProtocolFee(uint256 _protocolFee) external {
        require(msg.sender == feeCollector, "Not authorized");
        require(_protocolFee <= 500, "Fee too high"); // Max 5%
        protocolFee = _protocolFee;
    }

    function setFeeCollector(address _feeCollector) external {
        require(msg.sender == feeCollector, "Not authorized");
        if (_feeCollector == address(0)) revert InvalidAddress();
        feeCollector = _feeCollector;
    }

    // Allow contract to receive native USDC
    receive() external payable {}
}

